package co.edu.udea.compumovil.gr06_20211.lab2

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import co.edu.udea.compumovil.gr06_20211.lab2.data.DataBase
import co.edu.udea.compumovil.gr06_20211.lab2.model.Lugares
import co.edu.udea.compumovil.gr06_20211.lab2.ui.Adapter
import kotlinx.android.synthetic.main.fragment_lugares.*
import androidx.lifecycle.Observer
import androidx.recyclerview.widget.DividerItemDecoration
import androidx.recyclerview.widget.LinearLayoutManager

class LugaresFragment : Fragment(), Adapter.OnSitioClickListener {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

    }
    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        // Inflate the layout for this fragment
        return inflater.inflate(R.layout.fragment_lugares, container, false)
    }
    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        setupRecyclerView()

        val database = this.context?.let {
            DataBase.getDatabase(it)}

        val lista = listOf(

            Lugares("Peñol","data:image/jpg;base64,",
                "El peñón de Guatapé, o piedra del Peñol, es un monolito de 220 metros de altura, localizado en El Peñol-Guatapé, Antioquia, Colombia. La piedra de El Peñol es una masa granítica, compuesta por cuarzo, feldespato y mica, fue escalada por primera vez en 16 de julio de 1954 por un habitante de la zona, Luis Eduardo Villegas López. Hasta ahora la piedra sigue perteneciendo a la familia Villegas. Actualmente se puede acceder a su cumbre subiendo los 740 escalones construidos en el monolito.",
                "Temp 14°"),
            Lugares("Machu Picchu","data:image/jpg;base64,",
                "Machu Picchu es el nombre contemporáneo que se da a una llacta —antiguo poblado incaico andino— construida antes del siglo xv, ubicada en la Cordillera Oriental del sur de Perú, en la cadena montañosa de Los Andes a 2430 metros sobre el nivel del mar. Está ubicada en la región Cusco, provincia de Urubamba, distrito de Machupicchu, sobre el Valle Sagrado de los Incas, a 80 kilómetros al noroeste de la ciudad de Cusco, ciudad del Perú y por donde fluye el río Urubamba, río que atraviesa la cordillera y origina un cañón con clima de montaña tropical. Según muchos estudiosos su nombre original habría sido Llaqtapata.",
                "Temp 35°"),
            Lugares("Gran Muralla China en Mutianyu, Pekín","data:image/jpg;base64,",
                "Es el símbolo más reconocible de China, del voluntarismo de sus habitantes y también de su aislamiento secular. En una sencilla excursión desde Pekín puede caminarse por lo alto de algunos de los tramos mejor conservados de esta mole infinita que serpentea por las montañas del Norte del país.",
                "Temp 22°"),
            Lugares("Puente Golden Gate, San Francisco","data:image/jpg;base64,",
                "Pocas estructuras modernas han inspirado tantos guiones cinematográficos como esta sinfonía de acero y color anaranjado que cruza la bahía de San Francisco. Ni tanta literatura. De día, de noche y, como casi siempre, con niebla, esta proeza de la ingeniería que une esta gran ciudad con el condado de Mari en California, es también el más retratado.",
                "Temp 15°"),
            Lugares("Estatua de Cristo Redentor, Río de Janeiro","data:image/jpg;base64,",
                "Vigilante sobre la cima del monte Corcovado, a 709 metros de altitud, el que es el gran emblema brasileño observa con los brazos abiertos una de las panorámicas más impresionantes de la bahía de Guanabara.",
                "Temp 20°"),
            Lugares("Sydney Harbour Bridge, Sidney","data:image/jpg;base64,",
                "Es uno de los iconos de Sidney. Inaugurado en 1932 con el objetivo de unir el norte y el sur de la ciudad, fue durante mucho tiempo el puente en arco más largo del mundo. Hoy una de sus torres tiene un mirador desde donde se puede disfrutar de una panorámica excelente de la ciudad",
                "Temp 27°"),
            Lugares("Medellin","data:image/jpeg;base64,","Medellín es la capital de la provincia montañosa de Antioquia en Colombia. Es apodada la \"Ciudad de la eterna primavera\" por su clima templado y alberga la famosa Feria de las Flores anual.","Temp  22°"),
            Lugares("Roma","data:image/jpeg;base64,","Roma, la capital de Italia, es una extensa ciudad cosmopolita que tiene a la vista casi 3,000 años de arte, arquitectura y cultura de influencia mundial. Las ruinas antiguas como las del Foro y el Coliseo evocan el poder del antiguo Imperio Romano.","Temp  24°"),
            Lugares("Paris","data:image/jpeg;base64,","París, la capital de Francia, es una importante ciudad europea y un centro mundial del arte, la moda, la gastronomía y la cultura.","Temp 17°"),

            )


        if(database != null){
            database.lugares().getAll().observe(viewLifecycleOwner, Observer { result ->
                rv_lugares.adapter = Adapter(requireContext(), result+lista, this)
            })
        }

        btn_insert_sitio.setOnClickListener{
            findNavController().navigate(R.id.fragment_register_lugar)
        }

    }

    override fun OnSitioClick(lugares: Lugares) {
        val bundle = Bundle()
        bundle.putParcelable("lugares", lugares)
        findNavController().navigate(R.id.fragment_detalles_lugar, bundle)
    }

    private fun setupRecyclerView(){
        rv_lugares.layoutManager = LinearLayoutManager(requireContext())
        rv_lugares.addItemDecoration(
            DividerItemDecoration(requireContext(),
                DividerItemDecoration.VERTICAL)
        )
    }


}